CREATE OR REPLACE FUNCTION trigger_fct_tcrit_lock_trig() RETURNS trigger AS $BODY$
DECLARE
PRAGMA AUTONOMOUS_TRANSACTION;
testcaseid bigint;
lockState bigint;
BEGIN
  BEGIN

IF TG_OP = 'INSERT' THEN
testcaseid :=NEW.testcaseref;
ELSIF TG_OP = 'UPDATE' THEN
testcaseid :=OLD.testcaseref;
ELSIF TG_OP = 'DELETE' THEN
testcaseid :=OLD.testcaseref;
END IF;

   select vc_tftestsuite.islocked
   into STRICT lockstate
   from vc_tftestsuite,vc_tftestcase
   where vc_tftestsuite.id = vc_tftestcase.testsuiteref
   and
   vc_tftestcase.id = testcaseid;

   if
   	 lockstate != 0
   then
   	   RAISE EXCEPTION '%', 'Test Suite locked' USING ERRCODE = '45100';
   end if;

  END;
IF TG_OP = 'DELETE' THEN
	RETURN OLD;
ELSE
	RETURN NEW;
END IF;

END
$BODY$
