DROP TRIGGER IF EXISTS update_login ON vc_userlogininfo CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_update_login() RETURNS trigger AS $BODY$
declare developer bigint;
BEGIN
  select count(1) into STRICT developer from vc_developer where id = NEW.userref;
  -- if id not in developer table, then add login entry
  if developer = 0 then
           insert into VC_LOGIN values (NEW.userref,NEW.lastlogin,NEW.serververs);
  end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_update_login() FROM PUBLIC;

CREATE TRIGGER update_login
	AFTER INSERT OR UPDATE ON vc_userlogininfo FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_update_login();

