function getPhysicsOption(valence integer, kineticDesc varchar2) return integer as
     physicsOptions integer;
     isElectrical integer := 0;
     choice_MOLECULAR_ONLY integer  := 0;
     choice_BOTH integer            := 1;
     choice_ELECTRICAL_ONLY integer := 2;
BEGIN
     IF (instr(kineticDesc,'GHK')<>0 OR instr(kineticDesc,'Nernst')<>0 OR instr(kineticDesc,'GenCurr')<>0) THEN
