SET client_encoding TO 'UTF8';

SET search_path = vcell,public;
\set ON_ERROR_STOP ON

function getTaskDescription(simKey NUMBER, amt BINARY_INTEGER) return varchar2 as
     dataCLOB CLOB;
     pos INTEGER;
     buf VARCHAR(32767);
BEGIN
     SELECT data INTO dataCLOB FROM vc_taskdesc WHERE id = simKey;
     dbms_lob.open(dataCLOB, dbms_lob.lob_readonly);
     pos := 1;
     buf := '';
     buf := dbms_lob.substr(dataCLOB, amt, pos);
     dbms_lob.close(dataCLOB);
     return buf;
END;

 

