DROP TRIGGER IF EXISTS tcrit_lock_trig ON vc_tftestcriteria CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_tcrit_lock_trig() RETURNS trigger AS $BODY$
DECLARE
PRAGMA AUTONOMOUS_TRANSACTION;
testcaseid NUMBER;
lockState NUMBER;
BEGIN

IF INSERTING THEN
testcaseid :=:NEW.testcaseref;
ELSIF UPDATING THEN
testcaseid :=:OLD.testcaseref;
ELSIF DELETING THEN
testcaseid :=:OLD.testcaseref;
END IF;

   select vc_tftestsuite.islocked
   into lockstate
   from vc_tftestsuite,vc_tftestcase
   where vc_tftestsuite.id = vc_tftestcase.testsuiteref
   and
   vc_tftestcase.id = testcaseid;
   
   if
   	 lockstate != 0
   then
   	   raise_application_error(-20100,'Test Suite locked',true);
   end if;
   
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
