SET client_encoding TO 'UTF8';

SET search_path = vcell,public;
\set ON_ERROR_STOP ON

DROP TRIGGER IF EXISTS trslt_lock_trig ON vc_tftestresult CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_trslt_lock_trig() RETURNS trigger AS $BODY$
DECLARE
PRAGMA AUTONOMOUS_TRANSACTION;
testsuiteid NUMBER;
lockState NUMBER;
BEGIN

IF INSERTING THEN
testsuiteid :=:NEW.testcriteriaref;
ELSIF UPDATING THEN
testsuiteid :=:OLD.testcriteriaref;
ELSIF DELETING THEN
testsuiteid :=:OLD.testcriteriaref;
END IF;

   select vc_tftestsuite.islocked
   into lockstate
   from vc_tftestsuite,vc_tftestcase,vc_tftestcriteria
   where vc_tftestsuite.id = vc_tftestcase.testsuiteref
   and
   vc_tftestcase.id = vc_tftestcriteria.testcaseref
   and
   vc_tftestcriteria.id = testsuiteid;
   
   if
   	 lockstate != 0
   then
   	   raise_application_error(-20100,'Test Suite locked',true);
   end if;
   
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_trslt_lock_trig() FROM PUBLIC;

CREATE TRIGGER trslt_lock_trig
	BEFORE INSERT OR UPDATE OR DELETE ON vc_tftestresult 
 FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_trslt_lock_trig();

